mapper.update:
UPDATE sys_datainterface_organization
org_id = #{orgId}, org_user_name = #{orgUserName}, department_user_name = #{departmentUserName}, org_type = #{orgType}, department_id = #{departmentId}, public_key = #{publicKey}, private_key = #{privateKey}, ipv4 = #{ipv4}, access_token = #{accessToken}, expire_time = #{expireTime}, is_using = #{isUsing}, last_access_time = #{lastAccessTime}, last_update_user_id = #{lastUpdateUserId}, last_update_user_name = #{lastUpdateUserName}, last_update_time = #{lastUpdateTime},
WHERE id = #{id}

mapper.select:
SELECT ORG_ID
FROM SYS_DATAINTERFACE_ORGANIZATION t


		AND	t.ORG_ID = #{orgId}
		OR t.FATHER_ID = #{orgId}

select from sys_datainterface_organization t
t.org_type NOT IN (4, 7)
AND t.org_user_name = #{orgUserName}
