mapper.update:
UPDATE TRADE_PURCHASEORDERDETAIL
PURCHASEORDERID = #{purchaseorderid}, PROCURECATALOGID = #{procurecatalogid}, PURCHASECOUNT = #{purchasecount}, PURCHASEPRICE = #{purchaseprice}, PURCHASEAMOUNT = #{purchaseamount}, COMID_PS = #{comidPs}, COMNAME_PS = #{comnamePs}, COMID_TB = #{comidTb}, COMNAME_TB = #{comnameTb}, COMNAME_SC = #{comnameSc}, DETAILSTATUS = #{detailstatus}, DETAILCONTIME = #{detailcontime}, NDISRESON = #{ndisreson}, EDUPTIME = #{eduptime}, REFUSERETRESON = #{refuseretreson}, PODETAILID = #{podetailid}, PURCHASEDETAILCONTIME = #{purchasedetailcontime},
