mapper.select:
SELECT ,o.HOSID, o.PURCHASEORDERCODE, o.REMARK, o.ORDERTYPE, o.SUBMITTIME, tab_count.TOTALCOUNT detail_count,h.HOSPITALNAME FROM TRADE_PURCHASEORDERDETAIL t inner join TRADE_PURCHASEORDER o on t.PURCHASEORDERID = o.PURCHASEORDERID left join (select a.PURCHASEORDERID,count(*) TOTALCOUNT from TRADE_PURCHASEORDER a INNER join TRADE_PURCHASEORDERDETAIL b on b.PURCHASEORDERID=a.PURCHASEORDERID where b.COMID_PS IN
#{item}
GROUP BY a.PURCHASEORDERID ) tab_count on tab_count.PURCHASEORDERID= o.PURCHASEORDERID left join trade_hos h on h.id=o.HOSID
AND t.COMID_PS IN
#{item}
AND o.SUBMITTIME >= #{startTime}
AND #{endTime} >= o.SUBMITTIME
AND o.ORDERSTATUS = #{orderStatuses}
ORDER BY o.SUBMITTIME,t.PURCHASEORDERDETAILID DESC
